input_str1 = ""


def uniqueCharacters(str):
     
    # If at any time we encounter 2
    # same characters, return false
    for i in range(len(str)):
        for j in range(i + 1,len(str)):
            if(str[i] == str[j]):
                return False
 
    # If no duplicate characters
    # encountered, return true
    return True

def get_solution(input_str,start_index,end_index):
    for element in range(0, len(input_str)):
        if uniqueCharacters(input_str[start_index:end_index]):
            return end_index
        else:
            start_index+=1
            end_index+=1

def main():
    print("solution 1")
    start_index = 0
    end_index = 4
    print(get_solution(input_str1,start_index,end_index))
    print("solution 2")
    start_index = 0
    end_index = 14
    print(get_solution(input_str1,start_index,end_index))


if __name__ == "__main__":
    main()